import React, {Fragment} from 'react';
import {Link, Route} from 'react-router-dom';
import {NotFound} from '../../Errors';
import Text from '../Texts';
export default ({match: {url}, name, description, image, born, decased, texts}) =>
  <Fragment>
    <div>
      <img src={image} alt={name} style={{maxWidth:250}}></img>

      <h1>{name}</h1>

      <h3>{born} &mdash; {decased}</h3>

      <p>{description}</p>

      <ul>
        {texts.map(({id, title}) =>
        	<li>
            <Link to={`${url}/texts${id}`}>
              {title}
            </Link>
        	</li>
        )}
      </ul>


    <Route path={`${url}/texts/:textId`} render={
        props => {
          const text = texts.find(({id}) => id === props.match.params.textId)

          if(!text){
            return NotFound;
          }

          return <Text {...text} />;
        }
      }/>
    </div>
  </Fragment>
