import React,{Component} from 'react';
import PropTypes from 'prop-types';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import Checkbox from '@material-ui/core/Checkbox';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import withStyles from '@material-ui/core/styles/withStyles';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { fab } from '@fortawesome/free-brands-svg-icons'
import { library } from '@fortawesome/fontawesome-svg-core'
import {styles, button1, button2, button3}from '../styles/Login';
import '../css/Login.css';
import Navegation from '../views/Navegation';
import ReactDOM from 'react-dom';
import { BrowserRouter, Link} from 'react-router-dom';

library.add(fab);

class Login extends Component{
   constructor(props){
    super(props);
    this.state = {
      account:'',
      password:'',

    }

  this.handleSubmit = this.handleSubmit.bind(this);
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
   }

   handleSubmit(event) {
    const data = new FormData(event.target);

    fetch('https://api.geecko.com.mx/v1/auth/login',{
    method: 'POST',
    body: data,
    }).then((response) => {
      console.log(response.headers);
      if(response.count === 0){
        ReactDOM.render(
        alert("El usuario no existe"),
        document.getElementById('info')
      );
      }else{
        ReactDOM.render(
          <BrowserRouter>
           <Navegation/>
         </BrowserRouter>,
          document.getElementById('root')
        );
      }
      const access_token = response.headers.get('Authorization');
      localStorage.setItem("Authorization", access_token);
      return response.json()}
    ).then(auth => {
      console.log(auth);
      localStorage.setItem("token", auth.token);
    });

    this.input1.value = '';
    this.input2.value = '';
    event.preventDefault();
  }

  render(){
    const { classes } = this.props;
    const {account, password} = this.state;

    let content = (
      <div>
        <main className={classes.layout}>
          <CssBaseline/>
            <Paper className={classes.paper}>
              <Avatar className={classes.avatar}>
                <LockIcon />
              </Avatar>
              <Typography component="h1" variant="h5">
                Iniciar Sesión
              </Typography>
              <form className={classes.form}>
                <FormControl margin="normal" required fullWidth>
                  <InputLabel htmlFor="account">Usuario</InputLabel>
                  <Input id="account" name="account" autoComplete="account" autoFocus onChange={this.onChange} value={account}/>
                </FormControl>
                <FormControl margin="normal" required fullWidth>
                  <InputLabel htmlFor="password">Contraseña</InputLabel>
                  <Input name="password" type="password" id="password" autoComplete="current-password" />
                </FormControl>
                <FormControlLabel
                  control={<Checkbox value="recuerdame" color="primary" onChange={this.onChange}/>}
                  label="Recuerdame"
                />
                <MuiThemeProvider theme={button1}>
                  <Button  type="submit" fullWidth variant="contained" color="primary"className={classes.submit} onChange={this.onChange}>
                    Iniciar
                  </Button>
                </MuiThemeProvider>
                <div  variant="contained" className="strike" style={{padding:"1px"}}>
                   <span style={{color:'gray'}}><h4>O Inicia con</h4></span>
                </div>
                <MuiThemeProvider theme={button3}>
                 <Button fullWidth type="button"  variant="contained" color="primary" size="medium" className={classes.button}  onChange={this.onChange}>
                    <FontAwesomeIcon icon={['fab', 'google']}  pull="left" size="1x"/>Gmail
                 </Button>
               </MuiThemeProvider>
               <MuiThemeProvider theme={button2}>
                <Button fullWidth type="button"  variant="contained" color="primary" size="medium" className={classes.button2}  onChange={this.onChange}>
                   <FontAwesomeIcon icon={['fab', 'facebook']}  pull="left" size="1x"/>facebook
                </Button>
              </MuiThemeProvider>
              </form>
              <br/>
              <br/>
                <Link to='/Register' style={{color: '#2196f3'}} >¿Eres nuevo? Registrate</Link>
            </Paper>
        </main>
      </div>
    );
    return content;

  }
}
Login.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Login);
