import React, {Component} from 'react';
import ReactDOM from 'react-dom';
//Depedencias de material ui
import PropTypes from 'prop-types';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import withStyles from '@material-ui/core/styles/withStyles';
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import Navegation from '../Views/Navegation';
import AccountCircle from '@material-ui/icons/AccountCircle';
import InputAdornment from '@material-ui/core/InputAdornment';
import '../Styles/Login.css';
import { BrowserRouter, Link} from 'react-router-dom';
//Dependencias de incons font awesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { fab } from '@fortawesome/free-brands-svg-icons'
import { library } from '@fortawesome/fontawesome-svg-core'
import {styles,button1, button2, button3}from '../Styles/StylesLogin';

library.add(fab);

class Login extends Component{
  constructor(props){
    super(props);
    this.state = {
      account:'',
      password:'',

    }

  this.handleSubmit = this.handleSubmit.bind(this);
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
   }

   handleSubmit(event) {
    const data = new FormData(event.target);

    fetch('https://api.geecko.com.mx/v1/auth/login',{
    method: 'POST',
    body: data,
    }).then((response) => {
      console.log(response.headers);
      if(response.count === 0){
        ReactDOM.render(
        alert("El usuario no existe"),
        document.getElementById('info')
      );
      }else{
        ReactDOM.render(
          <BrowserRouter>
           <Navegation/>
         </BrowserRouter>,
          document.getElementById('root')
        );
      }
      const access_token = response.headers.get('Authorization');
      localStorage.setItem("Authorization", access_token);
      return response.json()}
    ).then(auth => {
      console.log(auth);
      localStorage.setItem("token", auth.token);
    });

    this.input1.value = '';
    this.input2.value = '';
    event.preventDefault();
  }

  render(){
    const { classes } = this.props;
    const {account, password} = this.state;
    return (
      <div>
        <CssBaseline />
        <main className={classes.layout}>
          <Paper className={classes.paper}>
            <Avatar className={classes.avatar}>
              <AccountCircle />
            </Avatar>
            <Typography component="h1" variant="h4">
              Iniciar sesión
            </Typography>
            <form className={classes.form} style={{textAlign:"center"}} onSubmit={this.handleSubmit}>
              <FormControl margin="normal" required fullWidth>
                <InputLabel htmlFor="Usuario">Usuario</InputLabel>
                <Input ref={(input) => {this.input1 = input;}} value={account} onChange = {this.onChange} 
                  startAdornment={
                    <InputAdornment position="start">
                      <AccountCircle color="primary"/>
                    </InputAdornment>
                  } type="text" name="account" id="account" autoComplete="lname" autoFocus />
              </FormControl>
              <FormControl margin="normal" required fullWidth>
                <InputLabel htmlFor="password">Contraseña</InputLabel>
                <Input ref={(input) => {this.input2 = input;}} value={password} onChange = {this.onChange} 
                  startAdornment={
                    <InputAdornment position="start"  variant="outlined">
                      <LockIcon  variant="contained" color="primary" />
                    </InputAdornment>
                  }type="password" name="password" id="password" autoComplete="lname"/>
              </FormControl>
              <MuiThemeProvider theme={button1}>
                <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}  onChange={this.onChange}>
                   Iniciar sesión  
                </Button>
              </MuiThemeProvider>
              <div  variant="contained" className="strike" style={{padding:"10px"}}>
                 <span style={{color:'gray'}}>O Inicia con</span>
              </div>
               <MuiThemeProvider theme={button3}>
                <Button fullWidth type="submit"  variant="contained" color="primary" size="medium" className={classes.submit}  onChange={this.onChange}>
                   <FontAwesomeIcon icon={['fab', 'google']}  pull="left" size="2x"/>Gmail
                </Button>
              </MuiThemeProvider>
              <MuiThemeProvider theme={button2}>
                <Button fullWidth type="submit"  variant="contained" color="primary" size="medium" className={classes.submit}  onChange={this.onChange}>
                   <FontAwesomeIcon icon={['fab', 'facebook']}  pull="left" size="2x"/>Facebook
                </Button>
              </MuiThemeProvider>
                <br/>
                <br/>
              <Link to='/Register' style={{color: '#2196f3'}} >¿Eres nuevo? Registrate</Link>
            </form>
          </Paper>
        </main>
      </div>
    );
  }
}

Login.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Login);
